mapper.select:

select no, option_parent_no, name
  from option_child
 where no = #{value }


select no, option_parent_no, name
  from option_child
 where option_parent_no = #{value }

mapper.update:

update option_child
	set option_parent_no=#{optionParentNo }, name=#{name } 
	where no=#{no }
