mapper.select:

select no, member_no as memberNo, status, cast(AES_DECRYPT(destination, #{aesKey}) as char) as destination, cast(AES_DECRYPT(phone, #{aesKey}) as char) as phone, order_date as orderDate
  from order_history
  where member_no=#{memberNo }


select no, member_no as memberNo, status, cast(AES_DECRYPT(destination, #{aesKey}) as char) as destination, cast(AES_DECRYPT(phone, #{aesKey}) as char) as phone, order_date as orderDate
  from order_history
  where no=#{no }
